import React,{useState} from 'react';
import classes from './EditMenu.module.css';
import {GetWallCreationStatus, SetWallCreationStatus, GetDeleteWallsStatus, SetDeleteWallStatus} from '../../Context/MenuSelectionContext';
const EditMenu = (props) => {
    const wallCreationStatus = GetWallCreationStatus();
    const toggleWallCreation = SetWallCreationStatus;
    const wallDeletionStatus = GetDeleteWallsStatus();
    const toggleWallDeletion = SetDeleteWallStatus;

    const [wallButtonOn, setWallButtonOn] = useState(false);
    const [deleteWallButtonOn, setDeleteWallButtonOn] = useState(false);

    const buttonOnStyle = {
        background: 'darkgray'
    };

    const buttonOffStyle = {
        background: 'lightblue'
    };

    const wallButtonPressedHandler = () => {
        props.wallControlOn(!wallButtonOn);
        toggleWallCreation(!wallButtonOn);
    };

    const deleteWallButtonPressedHandler = () => {
        props.deleteWallControlOn(!deleteWallButtonOn);
        setDeleteWallButtonOn(!deleteWallButtonOn);
    };

    return(
        <div className={`${props.className} ${classes.['menu-layout']}`}>
            <button 
             className={classes.['menu-btn']}
             style={wallButtonOn ? buttonOnStyle : buttonOffStyle}
             onClick={wallButtonPressedHandler}
             >
                 Create Walls
            </button>
            <button 
             className={classes.['menu-btn']}
             style={deleteWallButtonOn ? buttonOnStyle : buttonOffStyle}
             onClick={deleteWallButtonPressedHandler}
             >
                 Delete Walls
            </button>
        </div>
    );
};

export default EditMenu;
